function setTime(){const e=new Date,t=e.getSeconds(),n=t/60*360+90;secondsHand.style.transform="rotate("+n+"deg)";const s=e.getMinutes(),o=s/60*360+90;minutesHand.style.transform="rotate("+o+"deg)";const r=e.getHours(),d=r/12*360+90;hoursHand.style.transform="rotate("+d+"deg)"}const secondsHand=document.querySelector(".second-hand"),minutesHand=document.querySelector(".minutes-hand"),hoursHand=document.querySelector(".hours-hand");setInterval(setTime,1e3);